sys_variable_value.value:
(function execute(inputs, outputs) {
    var calculatorQry = 'sys_idIN' + inputs.calculator;
    var sirRec = new GlideRecord('sn_si_incident'); // script include looks for glideObject record to be passed
    sirRec.get(inputs.record.sys_id);
    var runCalcRule = new sn_sec_cmn.SecurityCalculator(sirRec, '', false, "sn_si_calculator_group", "sn_si_calculator", calculatorQry);
    runCalcRule.calculate(); // Run script include to execute calcutaors on demand.
    sirRec.update(); // system looks for update activity after calculator is applied.
})(inputs, outputs);

