sys_variable_value.value:
(function execute(inputs, outputs) {
   try {
    var articleSysId = inputs.articleSysId;
    
    if (!articleSysId) {
      throw new Error("Missing required input: articleSysId");
    }
    
    // Query kb_feedback using GlideAggregate
    var feedbackGR = new GlideAggregate('kb_feedback');
    feedbackGR.addQuery('article', articleSysId);
    feedbackGR.addAggregate('COUNT');
    feedbackGR.addAggregate('AVG', 'rating');
    feedbackGR.groupBy('article');  // Required when using aggregates
    feedbackGR.query();
    
    var totalfeedback = 0;
    var averagerating = 0;
    
    if (feedbackGR.next()) {
      totalfeedback = parseInt(feedbackGR.getAggregate('COUNT'), 10);
      var avgStr = feedbackGR.getAggregate('AVG', 'rating');
      averagerating = avgStr ? parseFloat(avgStr) : 0;
    } else {
      gs.info('No feedback found for article: ' + articleSysId);
    }
    
    // Collect comments
    var comments = [];
    var feedbackCommentsGR = new GlideRecord('kb_feedback');
    feedbackCommentsGR.addQuery('article', articleSysId);
    feedbackCommentsGR.addNotNullQuery('comments');
    feedbackCommentsGR.query();
    
    while (feedbackCommentsGR.next()) {
      comments.push(feedbackCommentsGR.getValue('comments'));
    }
    
    // Build summary
    var summary = 'Feedback Summary:\n';
    summary += '- Total feedback entries: ' + totalfeedback + '\n';
    summary += '- Average rating: ' + (averagerating ? averagerating.toFixed(2) : 'N/A') + '\n';
    summary += '- Comments:\n';
    
    if (comments.length > 0) {
      comments.forEach(function(comment, index) {
        summary += '  ' + (index + 1) + '. ' + comment + '\n';
      });
    } else {
      summary += '  No comments available.\n';
    }
    
    outputs.summary = summary;
    outputs.averagerating = averagerating;
    outputs.totalfeedback = totalfeedback;
    
    gs.info('Knowledge Article Feedback Summary generated for article: ' + articleSysId);
    
  } catch (ex) {
    var errorMsg = 'Error generating feedback summary: ' + ex.message;
    gs.error(errorMsg);
    outputs.summary = '';
    outputs.averagerating = 0;
    outputs.totalfeedback = 0;
  }
})(inputs, outputs);

