sys_variable_value.value:
(function execute(inputs, outputs) {
    try {
        // inputs
        var recordType = inputs.task_record;
        var userId = inputs.user;

        // Define table mapping configuration. can be enhanced for problem/change records too
        var mapping = {
            'Incident': {
                table: 'incident',
                userField: 'caller_id',
                excludeStates: [6, 7] // Resolved, Closed
            },
            'Work Order': {
                table: 'wm_order',
                userField: 'caller',
                excludeStates: [7, 3, 4] // Closed Complete, Closed Incomplete, Canceled
            },
            'Case': {
                table: 'sn_customerservice_case',
                userField: 'contact', // references customer_contact 
                excludeStates: [7, 6, 3] // Resolved, Closed, Canceled
            }
        };

        // Validate record type
        var config = mapping[recordType];
        if (!config) {
            outputs.action_status = 'error';
            outputs.error_type = 'validation';
            outputs.error_message = 'Invalid or unsupported record type.';
            outputs.ticket_numbers = '[]';
            outputs.ticket_count = 0;
            return;
        }

        // Query matching records
        var ticketRecord = new GlideAggregate(config.table);
        ticketRecord.addQuery(config.userField, userId);
        ticketRecord.addQuery('state', 'NOT IN', config.excludeStates.join(','));
        ticketRecord.groupBy('number');
        ticketRecord.orderByDesc('sys_created_on');
        ticketRecord.query();

        // ticket numbers
        var ticketNumbers = [];
        while (ticketRecord.next()) {
            ticketNumbers.push(ticketRecord.getValue('number'));
        }

        // outputs (Success)
        outputs.table_name = config.table;
        outputs.ticket_count = ticketNumbers.length;
        outputs.ticket_numbers = JSON.stringify(ticketNumbers);
        outputs.action_status = 'success';
        outputs.error_message = '';

    } 
    catch (error) {
        // runtime errors 
        outputs.table_name = '';
        outputs.ticket_count = 0;
        outputs.ticket_numbers = '[]';
        outputs.action_status = 'error';
        outputs.error_type = 'runtime';
        outputs.error_message = error.toString();
    }

})(inputs, outputs);

