sys_variable_value.value:
(function execute(inputs, outputs) {
    // Phase 1: Get Primary Records and Collect Join Keys
    var primaryRecords = [];
    var joinKeysMap = {}; // Use an object map to track unique keys
    var grPrimary = new GlideRecord(inputs.pTable);
    grPrimary.addEncodedQuery(inputs.pQuery);
    grPrimary.query();

    var primaryFieldsArray = inputs.pFields.split(',');
    
    while (grPrimary.next()) {
        var record = {};
        // Add primary fields
        primaryFieldsArray.forEach(function(field) {
            var fName = field.trim();
            record[fName] = grPrimary.getValue(fName);
        });
        
        // Collect the join key and store it in the map
        var keyValue = grPrimary.getValue(inputs.pJField);
        record.join_key_value = keyValue; // Store for later join
        
        // Add key to the map. The key of the map must be the key value itself.
        if (keyValue) {
           joinKeysMap[keyValue] = true;
        }
        
        primaryRecords.push(record);
    }
    
    // Convert the unique keys from the map keys into an array
    var uniqueJoinKeys = Object.keys(joinKeysMap);
    
    // Check for records to join
    if (uniqueJoinKeys.length === 0 || primaryRecords.length === 0) {
        outputs.joined_records = [];
        return;
    }
    
    // Phase 2: Get Joined Records using the Collected Keys
    var joinedRecordsMap = {}; // Map to hold joined data: {sys_id: {name: '...', email: '...'}}
    
    var grJoined = new GlideRecord(inputs.jTable);
    
    // Build the key query: joined_join_field IN (key1, key2, ...)
    var keyQuery = inputs.jJField + 'IN' + uniqueJoinKeys.join(',');
    
    // Add optional joined query
    if (inputs.jQuery) {
        keyQuery += '^' + inputs.jQuery;
    }
    
    grJoined.addEncodedQuery(keyQuery);
    grJoined.query();

    var joinedFieldsArray = inputs.jFields.split(',');
    
    while (grJoined.next()) {
        var joinedData = {};
        joinedFieldsArray.forEach(function(field) {
            var fName = field.trim();
            joinedData[fName] = grJoined.getValue(fName);
        });
        joinedRecordsMap[grJoined.getValue(inputs.jJField)] = joinedData;
    }

    // Phase 3: Combine and Format the Output
    var finalOutput = primaryRecords.map(function(primaryRec) {
    var joinedData = joinedRecordsMap[primaryRec.join_key_value] || {};
    
    // Manual merge
    var finalRecord = {};
    for (var key in primaryRec) {
        if (key !== 'join_key_value') {
            finalRecord[key] = primaryRec[key];
        }
    }
    for (var key in joinedData) {
        finalRecord[key] = joinedData[key];
    }
    return finalRecord;
});

// Serialize the Array of Objects into a JSON String
outputs.joined_records = JSON.stringify(finalOutput);

})(inputs, outputs);

