sys_variable_value.value:
(function execute(inputs, outputs) {

  try {
    var payload = (inputs.payload || '').toString();
    var secret = (inputs.secret || '').toString();
    var algo = (inputs.algorithm || 'HmacSHA256').toString();
    var secretEncoding = (inputs.secret_encoding || 'raw').toString().toLowerCase();

    if (!secret) throw new Error('secret is required');

    var secretB64 = (secretEncoding === 'base64') ? secret : gs.base64Encode(secret);

    var sigB64 = new CertificateEncryption().generateMac(secretB64, algo, payload);

    outputs.signature = sigB64;
  } catch (e) {
    outputs.signature = 'ERROR: ' + (e && e.message ? e.message : e);
  }
  
})(inputs, outputs);

