sys_variable_value.value:
(function execute(inputs, outputs) {

// 1. Check if the tag already exists
    var tagGR = new GlideRecord('label');
    //Used an exact match query for better performance. 
    tagGR.addQuery('name', inputs.tagName);
    tagGR.addActiveQuery();
    tagGR.query();

    if (tagGR.next()) {
        // Tag exists: Use the existing Sys ID
        outputs.tag_sys_id = tagGR.sys_id.toString();
        gs.info("Hacktoberfest: Found existing tag: " + inputs.tagName + " with Sys ID: " + outputs.tag_sys_id);
    } else {
        // Tag does not exist: Create a new tag
        tagGR.initialize();
        tagGR.setValue('name', inputs.tagName);
        tagGR.active = true;
        tagGR.setValue('color', 'red'); // Optional: Set a default color
    
        // Setting 'viewable_by' to 'everyone' ensures it's a shared/public tag.
        tagGR.setValue('viewable_by', 'everyone');

        // Insert the record and capture the Sys ID
        tagSysId = tagGR.insert(); 
        
        outputs.tag_sys_id = tagSysId.toString();
        gs.info("Hacktoberfest: Created new tag: " + inputs.tagName + " with Sys ID: " + outputs.tag_sys_id);
    }
})(inputs, outputs);

