sys_variable_value.value:
(function execute(inputs, outputs) {
    var flagged = [];
    var updateSetID = inputs.updateSetID;
    var updateSetGR = new GlideRecord('sys_update_set');
    updateSetGR.get('sys_id',updateSetID); // Get the update set for the provided ID.

    var updatesGR = new GlideRecord('sys_update_xml');
    updatesGR.get('update_set',updateSetID); // Get the updates for the update set provided.
    
    while(updatesGR.next()){
        if(updateSetGR.application != updatesGR.application){ // If the application doesn't match between update set and update
            flagged.push({
                id: updatesGR.getValue('sys_id'),
                name: updatesGR.getValue('name'),
                app: updatesGR.getValue('application'),
                createdBy: updatesGR.getValue('sys_created_by'),
                created: updatesGR.getValue('sys_created_on'),
                table: updatesGR.getValue('table'),
                target: updatesGR.getValue('target_name'),
                type: updatesGR.getValue('type')
            }); // Push stringified update record to flagged array
        }
    }

    outputs.flagged = flagged; // Assign output

})(inputs, outputs);

