sys_variable_value.value:
(function execute(inputs, outputs) {
    // --- Phase 1: Initialization and Input Retrieval --- 
    var tableName = inputs.table;
    var groupField = inputs.group_field;
    var assignedUserField = inputs.assigned_user_field;
    
    // Group (Reference) - Always use getValue('sys_id') for guaranteed Sys ID string.
    var groupSysId = inputs.group.getValue('sys_id'); 
    
     // Excluded Members (List.User) - Using the robust _next() iterator pattern.
    var excludedMembersInput = inputs.excluded_members;
    var excludedMembers = [];
    
    if(excludedMembersInput){
        while(excludedMembersInput._next()){
            excludedMembers.push(excludedMembersInput.getValue('sys_id'));
        }
    }
    
    // Output Initialization
    var minCount = Infinity;
    var bestUserSysId = '';
    var success = false;
    var errorMsg = '';

    try {
        // Prepare a GlideRecord instance for validation
        var gr = new GlideRecord(tableName);
        
        // --- Phase 2: Field Validation ---
        if (!gr.isValidField(groupField)) {
            throw new Error("Validation Failed: Group Field '" + groupField + "' does not exist on table '" + tableName + "'.");
        }

        if (!gr.isValidField(assignedUserField)) {
            throw new Error("Validation Failed: Assigned User Field '" + assignedUserField + "' does not exist on table '" + tableName + "'.");
        }
        
        // --- Phase 3: Member Identification and Filtering (TWO-STEP QUERY) ---
        var eligibleMembers = [];
        
        // STEP A: Get all members' Sys IDs.
        var memberGr = new GlideRecord('sys_user_grmember');
        memberGr.addQuery('group', groupSysId); 
        memberGr.query();

        var groupMemberSysIds = [];
        while (memberGr.next()) {
            groupMemberSysIds.push(memberGr.getValue('user'));
        }
        
        if (groupMemberSysIds.length === 0) {
             var groupName = inputs.group.getDisplayValue() || 'Unknown Group';
            throw new Error("Assignment Failed: Group '" + groupName + "' has no members.");
        }

        // STEP B: Get only the Active Users from the collected list.
        var userGr = new GlideRecord('sys_user');
        userGr.addQuery('sys_id', 'IN', groupMemberSysIds.join());
        userGr.addQuery('active', true);
        userGr.query();
        
        // STEP C: Filter Active Users against the Exclusion List.
        while (userGr.next()) {
            var userSysId = userGr.getValue('sys_id');

            if (excludedMembers.indexOf(userSysId) == -1) {
                eligibleMembers.push(userSysId);
            }
        }

        if (eligibleMembers.length === 0) {
            var groupName = inputs.group.getDisplayValue() || 'Unknown Group';
            throw new Error("Assignment Failed: No eligible active members found in group: " + groupName);
        }

        // --- Phase 4: Ticket Counting and Assignment (Least Busy) ---
        for (var i = 0; i < eligibleMembers.length; i++) {
            var userId = eligibleMembers[i];

            var ticketGr = new GlideAggregate(tableName);
            ticketGr.addAggregate('COUNT');
            ticketGr.addQuery('active', 'true');
            ticketGr.addQuery(assignedUserField, userId); 
            ticketGr.addQuery(groupField, groupSysId); 
            ticketGr.query();

            var count = 0;
            if (ticketGr.next()) {
                count = parseInt(ticketGr.getAggregate('COUNT'));
            }

            if (count < minCount) {
                minCount = count;
                bestUserSysId = userId;
            }
        }
        
        success = true;

    } catch (e) {
        errorMsg = (e.message) ? e.message : 'An unexpected and unhandled error occurred during assignment.';
        bestUserSysId = '';
        minCount = 0;
    }

    // --- Phase 5: Set Outputs ---
    outputs.assigned_user_sys_id = bestUserSysId;
    outputs.assignment_count = (isFinite(minCount) ? parseInt(minCount) : 0);
    outputs.success_status = success;
    outputs.error_message = errorMsg;

})(inputs, outputs);

