sys_variable_value.value:
(function execute(inputs, outputs) {
    try {
        var variables = inputs.requested_item.variables.getElements();
        var result = {};
        var pairs = inputs.variable_template.split('^');

        pairs.forEach(pair => {
            // Find the index of the first '=' character
            var equalIndex = pair.indexOf('=');

            // If '=' is found and it's not the first or last character
            if (equalIndex > 0 && equalIndex < pair.length - 1) {
                // Partition the key and value
                var key = pair.substring(0, equalIndex).trim();
                var value = pair.substring(equalIndex + 1).trim();

                // Store it in the result object
                result[key] = value;
            }
        });

        variables.forEach(function(variableRec) {
            var question = variableRec.getQuestion();
            if (!gs.nil(result[question.getName()])) {
                var varValue = result[question.getName()];
                if (varValue.startsWith('{')) {
                    varValue = JSON.parse(varValue).value;
                }
                question.setValue(varValue);
            }
        });

        inputs.requested_item.update();
        outputs.success = true;
        outputs.message = 'Successfully updated the variables of the Requested Item';
    } catch (e) {
        outputs.success = false;
        outputs.message = 'There is an error in Updating the variable of the Requested Item. Error:' + e;
    }

})(inputs, outputs);

