sys_variable_value.value:
(function execute(inputs, outputs) {
    try {

        //First check whether input column name exist in the table or not.
        var gr = new GlideRecord(inputs.inputTable);

        if (!gr.isValidField(inputs.inputFieldName)) { // if input column is not present then throw error
            throw new Error("Invalid field name: " + inputs.inputFieldName);
        }
        // if input column name is present then GlideAggregate and find the column value and count
        var countGa = new GlideAggregate(inputs.inputTable);
        if (inputs.inputCondition) {
            countGa.addEncodedQuery(inputs.inputCondition);
        }

        countGa.groupBy(inputs.inputFieldName);
        countGa.addAggregate("COUNT");
        countGa.query();

        var results = [];  // to store the column value and count
        while (countGa.next()) {
            var a = {};
            a.columnValue = countGa.getValue(inputs.inputFieldName);
            a.count = countGa.getAggregate('COUNT');
            results.push(a);
        }

        outputs.result = results;

    } catch (error) {
        // Re-throw the error in case of any other errors
        throw new Error("Flow Action Error: " + error.message);
    }
})(inputs, outputs);

