sys_variable_value.value:
(function execute(inputs, outputs) {
    var countGa = new GlideAggregate(inputs.inputTable);
    if (inputs.inputCondition) {
        countGa.addEncodedQuery(inputs.inputCondition);
    }
    countGa.addAggregate("COUNT");
    countGa.query();
    var count = 0;
    if (countGa.next()) {
        count = countGa.getAggregate("COUNT");
    }
    outputs.count = parseInt(count, 10) || 0;
})(inputs, outputs);

