sys_variable_value.value:
(function execute(inputs, outputs) {
    
    // Initialize outputs first
    outputs.success = false;
    outputs.total_scripts_analyzed = 0;
    outputs.compatibility_report = '{}';
    outputs.deprecated_apis_found = '[]';
    outputs.critical_issues = '[]';
    outputs.high_priority_issues = '[]';
    outputs.medium_priority_issues = '[]';
    outputs.low_priority_issues = '[]';
    outputs.affected_scripts = '[]';
    outputs.migration_suggestions = '[]';
    outputs.compatibility_score = 0;
    outputs.issues_summary = '{}';
    outputs.remediation_effort = '{}';
    outputs.error_message = '';
    
    // Input extraction (keeping the working version)
    function extractInput(inputName, alternatives) {
        alternatives = alternatives || [];
        var allNames = [inputName].concat(alternatives);
        
        for (var i = 0; i < allNames.length; i++) {
            var name = allNames[i];
            var value = null;
            
            if (inputs && inputs[name] !== undefined && inputs[name] !== null) {
                value = inputs[name];
            }
            
            if (typeof data !== 'undefined' && data[name] !== undefined && data[name] !== null) {
                value = data[name];
            }
            
            if (value !== null && value !== undefined) {
                var stringValue = value.toString().trim();
                if (stringValue !== '' && stringValue !== 'null' && stringValue !== 'undefined') {
                    return stringValue;
                }
            }
        }
        
        return '';
    }
    
    // Extract inputs
    var scriptSysIds = extractInput('script_sys_ids', ['script_sys_id']);
    var scriptContent = extractInput('script_content');
    var analysisScope = extractInput('analysis_scope');
    var applicationScope = extractInput('application_scope');
    var targetRelease = extractInput('target_release') || 'Zurich';
    
    // Validate inputs
    if (!scriptSysIds && !scriptContent && !analysisScope && !applicationScope) {
        outputs.error_message = "At least one input parameter is required";
        return;
    }
    
    try {
        gs.info("Starting compatibility analysis...");
        
        // Initialize compatibility checker
        var compatibilityChecker = {
            deprecated_apis: getDeprecatedApiDictionary(targetRelease),
            analyzed_scripts: [],
            issues: {
                critical: [],
                high: [],
                medium: [],
                low: []
            },
            total_scripts: 0
        };
        
        // Analyze direct script content
        if (scriptContent) {
            gs.info("Analyzing direct script content...");
            var directAnalysis = analyzeScriptContent(scriptContent, 'direct_input', 'Direct Input', compatibilityChecker.deprecated_apis);
            compatibilityChecker.analyzed_scripts.push(directAnalysis);
            categorizeIssues(directAnalysis.issues, compatibilityChecker.issues);
            compatibilityChecker.total_scripts++;
        }
        
        // Analyze specific script sys_ids
        if (scriptSysIds) {
            gs.info("Analyzing script sys_ids: " + scriptSysIds);
            var sysIdArray = scriptSysIds.split(',');
            
            for (var i = 0; i < sysIdArray.length; i++) {
                var sysId = sysIdArray[i].trim();
                
                if (sysId.length === 32) {
                    var scriptAnalysis = analyzeScriptBySysId(sysId, compatibilityChecker.deprecated_apis);
                    if (scriptAnalysis) {
                        compatibilityChecker.analyzed_scripts.push(scriptAnalysis);
                        categorizeIssues(scriptAnalysis.issues, compatibilityChecker.issues);
                        compatibilityChecker.total_scripts++;
                    }
                }
            }
        }
        
        // Analyze by scope
        if (analysisScope) {
            gs.info("Analyzing by scope: " + analysisScope);
            var scopeResults = analyzeByScope(analysisScope, applicationScope, compatibilityChecker.deprecated_apis);
            
            for (var j = 0; j < scopeResults.length; j++) {
                compatibilityChecker.analyzed_scripts.push(scopeResults[j]);
                categorizeIssues(scopeResults[j].issues, compatibilityChecker.issues);
                compatibilityChecker.total_scripts++;
            }
        }
        
        // Calculate results
        var totalIssues = compatibilityChecker.issues.critical.length + 
                         compatibilityChecker.issues.high.length + 
                         compatibilityChecker.issues.medium.length + 
                         compatibilityChecker.issues.low.length;
        
        var compatibilityScore = compatibilityChecker.total_scripts === 0 ? 100 : 
                                Math.max(0, 100 - Math.round((totalIssues / compatibilityChecker.total_scripts) * 10));
        
        // Prepare report
        var report = {
            analysis_metadata: {
                target_release: targetRelease,
                timestamp: new GlideDateTime().toString(),
                total_scripts: compatibilityChecker.total_scripts,
                total_issues: totalIssues,
                compatibility_score: compatibilityScore
            },
            analyzed_scripts: compatibilityChecker.analyzed_scripts,
            issues: compatibilityChecker.issues
        };
        
        // Set outputs
        outputs.success = true;
        outputs.total_scripts_analyzed = compatibilityChecker.total_scripts;
        outputs.compatibility_report = JSON.stringify(report, null, 2);
        outputs.critical_issues = JSON.stringify(compatibilityChecker.issues.critical, null, 2);
        outputs.high_priority_issues = JSON.stringify(compatibilityChecker.issues.high, null, 2);
        outputs.medium_priority_issues = JSON.stringify(compatibilityChecker.issues.medium, null, 2);
        outputs.low_priority_issues = JSON.stringify(compatibilityChecker.issues.low, null, 2);
        outputs.compatibility_score = compatibilityScore;
        outputs.affected_scripts = JSON.stringify(compatibilityChecker.analyzed_scripts.filter(function(s) { 
            return s.issues.length > 0; 
        }), null, 2);
        outputs.issues_summary = JSON.stringify({
            total: totalIssues,
            critical: compatibilityChecker.issues.critical.length,
            high: compatibilityChecker.issues.high.length,
            medium: compatibilityChecker.issues.medium.length,
            low: compatibilityChecker.issues.low.length
        }, null, 2);
        
        gs.info("Analysis completed successfully. Scripts: " + compatibilityChecker.total_scripts + ", Issues: " + totalIssues);
        
    } catch (error) {
        outputs.success = false;
        outputs.error_message = "Compatibility analysis error: " + error.message;
        gs.error("Upgrade Compatibility Checker Error: " + error.message);
        gs.error("Stack: " + error.stack);
    }
    
    // Helper Functions
    function getDeprecatedApiDictionary(targetRelease) {
        return [
            {
                api: 'GlideSession.getLanguage()',
                severity: 'medium',
                deprecated_in: 'quebec',
                removed_in: 'vancouver',
                replacement: 'gs.getUser().getLanguage()',
                description: 'GlideSession.getLanguage() is deprecated',
                migration_effort: 'low'
            },
            {
                api: 'JSUtil.nil(',
                severity: 'medium',
                deprecated_in: 'tokyo',
                removed_in: 'vancouver',
                replacement: 'gs.nil(',
                description: 'JSUtil.nil() is deprecated, use gs.nil() instead',
                migration_effort: 'low'
            },
            {
                api: 'eval(',
                severity: 'critical',
                deprecated_in: 'all',
                removed_in: 'vancouver',
                replacement: 'Refactor to avoid eval()',
                description: 'eval() is a security risk',
                migration_effort: 'high'
            },
            {
                api: 'GlideRecord.setWorkflow(',
                severity: 'high',
                deprecated_in: 'san_diego',
                removed_in: 'vancouver',
                replacement: 'GlideRecord.setEngineIfPresent(',
                description: 'setWorkflow() is deprecated',
                migration_effort: 'medium'
            },
            {
                api: 'GlideSystem.getProperty(',
                severity: 'low',
                deprecated_in: 'rome',
                removed_in: 'washington',
                replacement: 'gs.getProperty(',
                description: 'Use gs.getProperty() instead',
                migration_effort: 'low'
            },
            {
                api: 'current.setDisplayValue(',
                severity: 'high',
                deprecated_in: 'utah',
                removed_in: 'washington',
                replacement: 'current.setValue(',
                description: 'setDisplayValue() can cause data inconsistencies',
                migration_effort: 'medium'
            }
        ];
    }
    
    function analyzeScriptContent(content, id, name, deprecatedApis) {
        var analysis = {
            script_id: id,
            script_name: name,
            script_type: 'unknown',
            issues: [],
            lines_analyzed: content.split('\n').length,
            content_length: content.length
        };
        
        var lines = content.split('\n');
        
        for (var i = 0; i < deprecatedApis.length; i++) {
            var api = deprecatedApis[i];
            
            for (var lineNum = 0; lineNum < lines.length; lineNum++) {
                var line = lines[lineNum];
                
                if (line.indexOf(api.api) > -1) {
                    analysis.issues.push({
                        api_name: api.api,
                        severity: api.severity,
                        line_number: lineNum + 1,
                        line_content: line.trim(),
                        replacement: api.replacement,
                        description: api.description,
                        migration_effort: api.migration_effort
                    });
                }
            }
        }
        
        return analysis;
    }
    
    function analyzeScriptBySysId(sysId, deprecatedApis) {
        var scriptTables = [
            { table: 'sys_script', script_field: 'script', name_field: 'name', type: 'business_rule' },
            { table: 'sys_script_include', script_field: 'script', name_field: 'name', type: 'script_include' },
            { table: 'sys_script_client', script_field: 'script', name_field: 'name', type: 'client_script' },
            { table: 'sys_ui_script', script_field: 'script', name_field: 'name', type: 'ui_script' }
        ];
        
        for (var i = 0; i < scriptTables.length; i++) {
            var tableInfo = scriptTables[i];
            
            try {
                var gr = new GlideRecord(tableInfo.table);
                
                // Use get() method instead of query with setLimit
                if (gr.get(sysId)) {
                    var scriptContent = gr.getValue(tableInfo.script_field) || '';
                    var scriptName = gr.getValue(tableInfo.name_field) || 'Unknown';
                    
                    if (scriptContent.trim() === '') {
                        gs.info("Script content is empty for: " + scriptName);
                        continue;
                    }
                    
                    var analysis = analyzeScriptContent(scriptContent, sysId, scriptName, deprecatedApis);
                    analysis.script_type = tableInfo.type;
                    analysis.table_name = tableInfo.table;
                    
                    gs.info("Successfully analyzed script: " + scriptName + " (" + tableInfo.type + ")");
                    return analysis;
                }
            } catch (tableError) {
                gs.warn("Error accessing table " + tableInfo.table + ": " + tableError.message);
                continue;
            }
        }
        
        gs.warn("Script not found for sys_id: " + sysId);
        return null;
    }
    
    function analyzeByScope(scope, appScope, deprecatedApis) {
        var results = [];
        
        try {
            if (scope === 'business_rules' || scope === 'all') {
                var brResults = analyzeTableSafely('sys_script', 'script', 'name', 'business_rule', appScope, deprecatedApis);
                results = results.concat(brResults);
            }
            
            if (scope === 'script_includes' || scope === 'all') {
                var siResults = analyzeTableSafely('sys_script_include', 'script', 'name', 'script_include', appScope, deprecatedApis);
                results = results.concat(siResults);
            }
            
            if (scope === 'client_scripts' || scope === 'all') {
                var csResults = analyzeTableSafely('sys_script_client', 'script', 'name', 'client_script', appScope, deprecatedApis);
                results = results.concat(csResults);
            }
            
        } catch (scopeError) {
            gs.error("Error in scope analysis: " + scopeError.message);
        }
        
        return results;
    }
    
    function analyzeTableSafely(tableName, scriptField, nameField, scriptType, appScope, deprecatedApis) {
        var results = [];
        var recordCount = 0;
        var maxRecords = 25; // Reduced limit to prevent performance issues
        
        try {
            var gr = new GlideRecord(tableName);
            
            // Add application scope filter if specified
            if (appScope) {
                if (gr.isValidField('sys_scope')) {
                    gr.addQuery('sys_scope.scope', appScope);
                } else if (gr.isValidField('sys_package')) {
                    gr.addQuery('sys_package.source', appScope);
                }
            }
            
            // Add active filter where applicable
            if (gr.isValidField('active')) {
                gr.addQuery('active', true);
            }
            
            // Order by sys_created_on to get most recent first
            if (gr.isValidField('sys_created_on')) {
                gr.orderByDesc('sys_created_on');
            }
            
            // Execute query
            gr.query();
            
            while (gr.next() && recordCount < maxRecords) {
                try {
                    var scriptContent = gr.getValue(scriptField) || '';
                    if (scriptContent.trim() === '') {
                        continue;
                    }
                    
                    var scriptName = gr.getValue(nameField) || 'Unknown';
                    var sysId = gr.getValue('sys_id');
                    
                    var analysis = analyzeScriptContent(scriptContent, sysId, scriptName, deprecatedApis);
                    analysis.script_type = scriptType;
                    analysis.table_name = tableName;
                    
                    // Only include scripts with issues to reduce noise
                    if (analysis.issues.length > 0) {
                        results.push(analysis);
                    }
                    
                    recordCount++;
                    
                } catch (recordError) {
                    gs.warn("Error processing record in " + tableName + ": " + recordError.message);
                    continue;
                }
            }
            
            gs.info("Analyzed " + recordCount + " records from " + tableName + ", found " + results.length + " with issues");
            
        } catch (tableError) {
            gs.error("Error analyzing table " + tableName + ": " + tableError.message);
        }
        
        return results;
    }
    
    function categorizeIssues(scriptIssues, issueCategories) {
        for (var i = 0; i < scriptIssues.length; i++) {
            var issue = scriptIssues[i];
            switch (issue.severity) {
                case 'critical':
                    issueCategories.critical.push(issue);
                    break;
                case 'high':
                    issueCategories.high.push(issue);
                    break;
                case 'medium':
                    issueCategories.medium.push(issue);
                    break;
                case 'low':
                    issueCategories.low.push(issue);
                    break;
                default:
                    issueCategories.low.push(issue);
                    break;
            }
        }
    }
    
})(inputs, outputs);

