sys_variable_value.value:
(function execute(inputs, outputs) {

var recordGR = new GlideRecord(inputs.table_name);

    if (!recordGR.get(inputs.record_sys_id)) {
        outputs.error = "Error: Record with Sys ID '" + inputs.record_sys_id + "' not found in table '" + inputs.table_name + "'.";
         throw new Error('Record not found in table: ' + inputs.tableName);
    }
    var instanceName = gs.getProperty('instance_name');

    // Query attachments
    var attachmentGR = new GlideRecord('sys_attachment');
    attachmentGR.addQuery('table_name', inputs.table_name);
    attachmentGR.addQuery('table_sys_id', inputs.record_sys_id);
    attachmentGR.query();

    var attachments = [];

    while (attachmentGR.next()) {
        var link='https://'+instanceName+'.service-now.com/sys_attachment.do?sys_id=' + attachmentGR.getUniqueValue();
        attachments.push({
            name: attachmentGR.getValue('file_name'),
            size_bytes: attachmentGR.getValue('size_bytes'),
            content_type: attachmentGR.getValue('content_type'),
            download_link: link
        });
    }

    // Handle no attachments found
    if (attachments.length === 0) {
        outputs.error = "No attachments found for record '" + inputs.record_sys_id + "' in table '" + inputs.table_name + "'.";
         throw new Error('Record Attachment found for given Input table and Record');
    }

    // Return metadata
    outputs.attachments_json = JSON.stringify(attachments);
})(inputs, outputs);

